import React, { useState } from "react";
import List from "../../components/List/List";
import { useParams } from "react-router-dom";
import "./Products.scss";
import useFetch from "../../hooks/useFetch";

const Products = () => {
	const catId = parseInt(useParams().id);
	const [maxPrice, setMaxPrice] = useState(1000);
	const [sort, setSort] = useState(null);
	const [selectedSubCats, setSelectedSubCats] = useState([]);

	const { data, loading, error } = useFetch(
		`/sub-categories?[filters][categories][id][$eq]=${catId}`
	);
	const handleChange = (e) => {
		const value = e.target.value;
		const isChecked = e.target.chacked;

		setSelectedSubCats(
			isChecked
				? [...selectedSubCats, value]
				: selectedSubCats.filter((item) => item !== value)
		);
		console.log(selectedSubCats);
	};

	return (
		<div className="products">
			<div className="left">
				<div className="filterItem">
					<h2>Product Categories</h2>
					{data?.map((item) => (
						<div className="inputItem" key={item.id}>
							<input
								type="checkbox"
								id={item.id}
								value={item.id}
								onChange={handleChange}
							/>
							<label htmlFor={item.id}>
								{item.attributes.title}
							</label>
						</div>
					))}
				</div>
				<div className="filterItem">
					<h2>Filter By Price</h2>
					<div className="inputItem">
						<span>0</span>
						<input
							type="range"
							min={0}
							max={1000}
							onChange={(e) => setMaxPrice(e.target.value)}
						/>
						<span>{maxPrice}</span>
					</div>
				</div>
				<div className="filterItem">
					<h2>Sort By</h2>
					<div className="inputItem">
						<input
							type="radio"
							name="price"
							id="asc"
							value="asc"
							onChange={(e) => setSort("asc")}
						/>
						<label htmlFor="asc">Lowest Price</label>
					</div>
					<div className="inputItem">
						<input
							type="radio"
							name="price"
							id="desc"
							value="desc"
							onChange={(e) => setSort("desc")}
						/>
						<label htmlFor="desc">Highst Price</label>
					</div>
				</div>
			</div>
			<div className="right">
				<img
					src="https://images.pexels.com/photos/1488463/pexels-photo-1488463.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2"
					className="catImg"
					alt=""
				/>
				<List catId={catId} maxPrice={maxPrice} sort={sort} />
			</div>
		</div>
	);
};

export default Products;
